yarn.nodemanager.local-dirs={{nm_local_dirs}}
yarn.nodemanager.log-dirs={{nm_log_dirs}}
yarn.nodemanager.linux-container-executor.group={{yarn_executor_container_group}}
banned.users=hdfs,yarn,mapred,bin
min.user.id={{min_user_id}}

{{ '[docker]' }}
  module.enabled={{docker_module_enabled}}
  docker.binary={{docker_binary}}
  docker.allowed.capabilities={{docker_allowed_capabilities}}
  docker.allowed.devices={{docker_allowed_devices}}
  docker.allowed.networks={{docker_allowed_networks}}
  docker.allowed.ro-mounts={{nm_local_dirs}},{{docker_allowed_ro_mounts}}
  docker.allowed.rw-mounts={{nm_local_dirs}},{{nm_log_dirs}},{{docker_allowed_rw_mounts}}
  docker.privileged-containers.enabled={{docker_privileged_containers_enabled}}
  docker.trusted.registries={{docker_trusted_registries}}
  docker.allowed.volume-drivers={{docker_allowed_volume_drivers}}

{{ '[gpu]' }}
  module.enabled={{gpu_module_enabled}}

{{ '[cgroups]' }}
  root={{cgroup_root}}
  yarn-hierarchy={{yarn_hierarchy}}

# Set environment variables here.

# AMS instance name
export AMS_INSTANCE_NAME={{hostname}}

# The java implementation to use. Java 1.6 required.
export JAVA_HOME={{java64_home}}

# Collector Log directory for log4j
export AMS_COLLECTOR_LOG_DIR={{ams_collector_log_dir}}

# Monitor Log directory for outfile
export AMS_MONITOR_LOG_DIR={{ams_monitor_log_dir}}

# Collector pid directory
export AMS_COLLECTOR_PID_DIR={{ams_collector_pid_dir}}

# Monitor pid directory
export AMS_MONITOR_PID_DIR={{ams_monitor_pid_dir}}

# AMS HBase pid directory
export AMS_HBASE_PID_DIR={{hbase_pid_dir}}

# AMS Collector heapsize
export AMS_COLLECTOR_HEAPSIZE={{metrics_collector_heapsize}}

# HBase Tables Initialization check enabled
export AMS_HBASE_INIT_CHECK_ENABLED={{ams_hbase_init_check_enabled}}

# AMS Collector options
export AMS_COLLECTOR_OPTS="-Djava.library.path=/usr/lib/ams-hbase/lib/hadoop-native"
{% if security_enabled %}
export AMS_COLLECTOR_OPTS="$AMS_COLLECTOR_OPTS -Dzookeeper.sasl.client.username={{zk_principal_user}} -Djava.security.auth.login.config={{ams_collector_jaas_config_file}}"
{% endif %}

# AMS Collector GC options
export AMS_COLLECTOR_GC_OPTS="-XX:+UseConcMarkSweepGC -verbose:gc -XX:+PrintGCDetails -XX:+PrintGCDateStamps -Xloggc:{{ams_collector_log_dir}}/collector-gc.log-`date +'%Y%m%d%H%M'`"
export AMS_COLLECTOR_OPTS="$AMS_COLLECTOR_OPTS $AMS_COLLECTOR_GC_OPTS"

# Metrics collector host will be blacklisted for specified number of seconds if metric monitor failed to connect to it.
export AMS_FAILOVER_STRATEGY_BLACKLISTED_INTERVAL={{failover_strategy_blacklisted_interval}}

# Extra Java CLASSPATH elements for Metrics Collector. Optional.
export COLLECTOR_ADDITIONAL_CLASSPATH={{ams_classpath_additional}}

{% if has_ganglia_server %}
*.period=60

*.sink.ganglia.class=org.apache.hadoop.metrics2.sink.ganglia.GangliaSink31
*.sink.ganglia.period=10

# default for supportsparse is false
*.sink.ganglia.supportsparse=true

.sink.ganglia.slope=jvm.metrics.gcCount=zero,jvm.metrics.memHeapUsedM=both
.sink.ganglia.dmax=jvm.metrics.threadsBlocked=70,jvm.metrics.memHeapUsedM=40

# Hook up to the server
namenode.sink.ganglia.servers={{ganglia_server_host}}:8661
datanode.sink.ganglia.servers={{ganglia_server_host}}:8659
jobtracker.sink.ganglia.servers={{ganglia_server_host}}:8662
tasktracker.sink.ganglia.servers={{ganglia_server_host}}:8658
maptask.sink.ganglia.servers={{ganglia_server_host}}:8660
reducetask.sink.ganglia.servers={{ganglia_server_host}}:8660
resourcemanager.sink.ganglia.servers={{ganglia_server_host}}:8664
nodemanager.sink.ganglia.servers={{ganglia_server_host}}:8657
historyserver.sink.ganglia.servers={{ganglia_server_host}}:8666
journalnode.sink.ganglia.servers={{ganglia_server_host}}:8654
nimbus.sink.ganglia.servers={{ganglia_server_host}}:8649
supervisor.sink.ganglia.servers={{ganglia_server_host}}:8650

resourcemanager.sink.ganglia.tagsForPrefix.yarn=Queue

{% endif %}

{% if has_metric_collector %}

*.period={{metrics_collection_period}}
*.sink.timeline.plugin.urls=file:///usr/lib/ambari-metrics-hadoop-sink/ambari-metrics-hadoop-sink.jar
*.sink.timeline.class=org.apache.hadoop.metrics2.sink.timeline.HadoopTimelineMetricsSink
*.sink.timeline.period={{metrics_collection_period}}
*.sink.timeline.sendInterval={{metrics_report_interval}}000
*.sink.timeline.slave.host.name={{hostname}}
*.sink.timeline.zookeeper.quorum={{zookeeper_quorum}}
*.sink.timeline.protocol={{metric_collector_protocol}}
*.sink.timeline.port={{metric_collector_port}}
*.sink.timeline.instanceId = {{cluster_name}}
*.sink.timeline.set.instanceId = {{set_instanceId}}
*.sink.timeline.host_in_memory_aggregation = {{host_in_memory_aggregation}}
*.sink.timeline.host_in_memory_aggregation_port = {{host_in_memory_aggregation_port}}
{% if is_aggregation_https_enabled %}
*.sink.timeline.host_in_memory_aggregation_protocol = {{host_in_memory_aggregation_protocol}}
{% endif %}

# HTTPS properties
*.sink.timeline.truststore.path = {{metric_truststore_path}}
*.sink.timeline.truststore.type = {{metric_truststore_type}}
*.sink.timeline.truststore.password = {{metric_truststore_password}}

datanode.sink.timeline.collector.hosts={{ams_collector_hosts}}
namenode.sink.timeline.collector.hosts={{ams_collector_hosts}}
resourcemanager.sink.timeline.collector.hosts={{ams_collector_hosts}}
nodemanager.sink.timeline.collector.hosts={{ams_collector_hosts}}
jobhistoryserver.sink.timeline.collector.hosts={{ams_collector_hosts}}
journalnode.sink.timeline.collector.hosts={{ams_collector_hosts}}
maptask.sink.timeline.collector.hosts={{ams_collector_hosts}}
reducetask.sink.timeline.collector.hosts={{ams_collector_hosts}}
applicationhistoryserver.sink.timeline.collector.hosts={{ams_collector_hosts}}

resourcemanager.sink.timeline.tagsForPrefix.yarn=Queue

{% if is_nn_client_port_configured %}
# Namenode rpc ports customization
namenode.sink.timeline.metric.rpc.client.port={{nn_rpc_client_port}}
{% endif %}
{% if is_nn_dn_port_configured %}
namenode.sink.timeline.metric.rpc.datanode.port={{nn_rpc_dn_port}}
{% endif %}
{% if is_nn_healthcheck_port_configured %}
namenode.sink.timeline.metric.rpc.healthcheck.port={{nn_rpc_healthcheck_port}}
{% endif %}

{% endif %}

export JAVA_HOME={{java64_home}}
export ZOOKEEPER_HOME={{zk_home}}
export ZOO_LOG_DIR={{zk_log_dir}}
export ZOOPIDFILE={{zk_pid_file}}
export SERVER_JVMFLAGS={{zk_server_heapsize}}
export JAVA=$JAVA_HOME/bin/java
export CLASSPATH=$CLASSPATH:/usr/share/zookeeper/*

{% if security_enabled %}
export SERVER_JVMFLAGS="$SERVER_JVMFLAGS -Djava.security.auth.login.config={{zk_server_jaas_file}}"
export CLIENT_JVMFLAGS="$CLIENT_JVMFLAGS -Djava.security.auth.login.config={{zk_client_jaas_file}} -Dzookeeper.sasl.client.username={{zk_principal_user}}"
{% endif %}

# export JAVA_HOME=/home/y/libexec/jdk1.8.0/

export HADOOP_JOB_HISTORYSERVER_HEAPSIZE={{jobhistory_heapsize}}

# We need to add the RFA appender for the mr daemons only;
# however, HADOOP_MAPRED_LOGGER is shared by the mapred client and the
# daemons. This will restrict the RFA appender to daemons only.
export HADOOP_LOGLEVEL=${HADOOP_LOGLEVEL:-INFO}
export HADOOP_ROOT_LOGGER=${HADOOP_ROOT_LOGGER:-INFO,console}
export HADOOP_DAEMON_ROOT_LOGGER=${HADOOP_DAEMON_ROOT_LOGGER:-${HADOOP_LOGLEVEL},RFA}

{% if security_enabled %}
export MAPRED_HISTORYSERVER_OPTS="-Djava.security.auth.login.config={{mapred_jaas_file}}  -Djavax.security.auth.useSubjectCredsOnly=false"
{% endif %}

#export HADOOP_JHS_LOGGER=INFO,RFA # Hadoop JobSummary logger.
#export HADOOP_IDENT_STRING= #A string representing this instance of hadoop. $USER by default
#export HADOOP_NICENESS= #The scheduling priority for daemons. Defaults to 0.
export HADOOP_OPTS="-Dhdp.version=$HDP_VERSION $HADOOP_OPTS"
export HADOOP_OPTS="-Djava.io.tmpdir={{hadoop_java_io_tmpdir}} $HADOOP_OPTS"
export JAVA_LIBRARY_PATH="${JAVA_LIBRARY_PATH}:{{hadoop_java_io_tmpdir}}"

# History server logs
export HADOOP_LOG_DIR={{mapred_log_dir_prefix}}/$USER

# History server pid
export HADOOP_PID_DIR={{mapred_pid_dir_prefix}}/$USER

